var cv = document.getElementById('cv').getContext("2d");
	cv.mozImageSmoothingEnabled = false;
	var TILE = 32;

	var land = [];
	var tree = [];
	var object = [];
	var onLand = [];
	var MAP_HEIGHT = 15;
	var MAP_WIDTH = 30;
	var POINT = 15*TILE;
	var FLOOR = 14;
	var teepee = {
			src : 'data:image/jpeg;base64,',
			height : 4,
			width : 3,
			name : 'teepee',
			id : '0004'
		}

	function generateWorld() {
		var MAP = 1;
		var num_tree = 3;
		var num_berries = 7;
		var num_teepee = 1;
		var num_rock = 1;
		var num_tree2 = 1;
		numrand = 0;
		//(Math.random() * 10) + 1
		
		//craete land
		for(var i=0;i<MAP_WIDTH;i++){
			land[i] = {
				id : '0001',
				name : 'normal_land',
				color : '#508748',
				x : i,
				y : FLOOR,
				height:'1',
				width:'1'
			};
			object[i] = {
				id : '0000',
				name : 'nothing',
				color : '#ffffff',
				x : i,
				y : FLOOR-1,
				height:'1',
				width:'1'
			};
			onLand[i] = object[i].id;
		}

		//craete tree
		numrand = 0;
		do{
			var pos = Math.floor((Math.random() * (MAP_WIDTH-3)) + 1); // dont get first and last tile -= width
			if(check_nothing(pos,2)){
				object[pos] = {
					src : 'data:image/png;base64,',
					id : '0002',
					name : 'berries',
					color : '#ccc8ce',
					x : pos,
					y : FLOOR-1,
					height:'2',
					width:'2'
				};
				for(var i = 0;i<2;i++){
					onLand[pos+i] = object[pos].id;
				}
				num_tree--;
			}else{
				numrand++;
			}
		}while(num_tree>0 && numrand!=50);

		do{
			var pos = Math.floor((Math.random() * (MAP_WIDTH-3)) + 1); // dont get first and last tile -= width
			if(check_nothing(pos,3)){
				object[pos] = {
					src : 'data:image/png;base64,',
					id : '0005',
					name : 'rock',
					color : '#ccc8ce',
					x : pos,
					y : FLOOR-1,
					height:'3',
					width:'3'
				};
				for(var i = 0;i<3;i++){
					onLand[pos+i] = object[pos].id;
				}
				num_rock--;
			}else{
				numrand++;
			}
		}while(num_rock>0 && numrand!=50);

		do{
			var pos = Math.floor((Math.random() * (MAP_WIDTH-3)) + 1); // dont get first and last tile -= width
			if(check_nothing(pos,2)){
				object[pos] = {
					src : 'data:image/png;base64,',
					id : '0006',
					name : 'tree',
					color : '#ccc8ce',
					x : pos,
					y : FLOOR-1,
					height:'6',
					width:'2'
				};
				for(var i = 0;i<2;i++){
					onLand[pos+i] = object[pos].id;
				}
				num_tree2--;
			}else{
				numrand++;
			}
		}while(num_tree2>0 && numrand!=50);

		do{
			var pos = Math.floor((Math.random() * (MAP_WIDTH-1)) + 1); // dont get first and last tile //confighere
			if(check_nothing(pos,1)){ //confighere
				object[pos] = { 
					src : 'data:image/png;base64,',
					id : '0003',
					name : 'berries',
					color : '#7e8899',
					x : pos,
					y : FLOOR-1,
					height:'1', //confighere
					width:'1' //confighere
				};
				for(var i = 0;i<1;i++) //confighere
					onLand[pos+i] = object[pos].id;
				num_berries--;
			}else{
				numrand++;
			}
		}while(num_berries>0 && numrand!=50);

		do{
			var pos = Math.floor((Math.random() * (MAP_WIDTH-3)) + 1); // dont get first and last tile //confighere
			if(check_nothing(pos,3)){
				object[pos] = { 
					id : '0004',
					name : 'teepee',
					color : '#ffffff',
					x : pos,
					y : FLOOR-1,
					height:'4', //confighere
					width:'3' //confighere
				};
				teepee.x = pos;
				teepee.y = FLOOR-1;
				for(var i = 0;i<3;i++) //confighere
					onLand[pos+i] = object[pos].id;
				num_teepee--;
			}else{
				numrand++;
			}
		}while(num_teepee>0 && numrand!=50);
	}


	function check_nothing(argu,length){
		for(var i =0;i<length;i++){
			if(onLand[argu+i]!="0000"){
				return 0;
			}
		}
		return 1;
	}

	player = { 
				src : 'data:image/png;base64,',
				id : 'pppp',
				name : 'player',
				color : '#9b1885',
				x : 15*TILE,
				y : FLOOR-1,
				height:'2',
				width:'1',
				speed: 5
			};

	bf = { 
				src : 'data:image/png;base64,',
				id : 'bbbb',
				name : 'buffalo',
				color : '#9b1885',
				x : 17*TILE,
				y : FLOOR-1,
				height:'2',
				width:'2',
				speed: 4
			};

	bear = { 
				src : 'data:image/png;base64,',
				id : 'bebe',
				name : 'bear',
				color : '#9b1885',
				x : 10*TILE,
				y : FLOOR-4,
				height:'5',
				width:'4',
				speed: 2
			};

	generateWorld();
	//console.log(land.length);
	var total = 1;

	//draw map
	function drawMap(){
		for(var i = 0;i<land.length;i++){
			cv.fillStyle = land[i].color;
			cv.globalAlpha = 0.8;
			cv.fillRect (land[i].x*TILE,land[i].y*TILE,land[i].height*TILE,land[i].width*TILE);
		}

		//console.log(object);
		for(var i = 0;i<object.length;i++){
			cv.fillStyle = object[i].color;
			cv.globalAlpha = 0.8;
			if(onLand[i]==object[i].id){
				if(object[i].src!=null){
					var image = new Image();
					image.src = object[i].src;
					cv.drawImage(image, object[i].x*TILE, (object[i].y-object[i].height+1)*TILE, object[i].width*TILE, object[i].height*TILE);
				}else{
					cv.fillRect (object[i].x*TILE,(object[i].y-object[i].height+1)*TILE,object[i].width*TILE,object[i].height*TILE);
				}
			}
		}

		var Img = new Image();
		cv.globalAlpha = 1;
		Img.src = teepee.src;
		cv.drawImage(Img, teepee.x*TILE, (teepee.y-teepee.height+1)*TILE, teepee.width*TILE, teepee.height*TILE);
	}

	var super_update = self.update;
	self.update = function(){
		super_update();
		if(self.pressingRight || self.pressingLeft)
			self.spriteAnimCounter += 0.25;
		if(self.pressingMouseLeft)
			self.performAttack();
		if(self.pressingMouseRight)
			self.performSpecialAttack();
	}	

	//draw player
	function updateplayer(){
		var center = parseInt(($(window).width())/2);
		if(center > 500){
			center = 500;
		}
		if((center - player.x > player.speed || player.x - center > player.speed)&&(player.x < 500)){
			if(player.x < center){
				player.x+= player.speed;
				bf.x += bf.speed;
				bear.x += bear.speed;
			}
			else if(player.x > center){
				player.x-= player.speed;
				bf.x -= bf.speed;
				bear.x -= bear.speed;
			}
		}else{
			if(player.x < center){
				player.x+= 1;
				bf.x += 1;
				bear.x += 1;
			}
			else if(player.x > center){
				player.x-= 1;
				bf.x -= 1;
				bear.x -= 1;
			}
		}


		//cv.fillStyle = '#000';
		//cv.globalAlpha = 0.8;
		var image = new Image();
		image.src = bear.src;
		cv.drawImage(image, bear.x-(TILE/2), (bear.y-player.height+1)*TILE, bear.width*TILE, bear.height*TILE);
		
		var image = new Image();
		image.src = bf.src;
		cv.drawImage(image, bf.x-(TILE/2), (bf.y-player.height+1)*TILE, bf.width*TILE, bf.height*TILE);
		
		var image = new Image();
			image.src = player.src;
			cv.drawImage(image, player.x-(TILE/2)-(80), (player.y-player.height+1)*TILE, player.width*TILE, player.height*TILE);

		for(i = 0;i<total;i++){
			var image = new Image();
			image.src = player.src;
			cv.drawImage(image, player.x-(TILE/2)+(30*i), (player.y-player.height+1)*TILE, player.width*TILE, player.height*TILE);
		}
		//cv.fillRect (player.x-(TILE/2),(player.y-player.height+1)*TILE,player.width*TILE,player.height*TILE);
		if(player.x>(MAP_WIDTH-1)*TILE){
			generateWorld();
			player.x = TILE+10;
			bf.x = player.x - 20;
			POINT = player.x;
		}
		else if(player.x<TILE){
			generateWorld();
			player.x = (MAP_WIDTH-1)*TILE-10;
			bf.x = player.x - 20;
			POINT = player.x;
		}
	}

	textnum = 0;
	setInterval(update,40);
	var chat = ['สวัสดี !','ขอโทษทีตอนนี้ฉันฟังไม่รู้เรื่อง :(','.....','ลา มะ ลิ ลา','กินข้าวต้องเคี้ยวให้ละเอียด','อย่าลืมถอดรองเท้าก่อนอาบน้ำ'];
	function update(){

		cv.clearRect(0,0,MAP_WIDTH*TILE,MAP_HEIGHT*TILE)
		drawMap();
		updateplayer();
		cv.font="18px Georgia";
		cv.fillStyle = 'red';
		cv.fillStyle = 'green';
		cv.fillText("Welcome to my world !",player.x-100,(player.y-player.height)*TILE-200);
		cv.font="16px Georgia";
		cv.fillStyle = '#ddd';
		cv.fillText("Pruek Bot ",player.x-100,(player.y-player.height)*TILE-170);
		cv.fillText("with "+total+" user now",player.x-100,(player.y-player.height)*TILE-140);
		cv.fillStyle = '#555';
		cv.fillText('( " '+chat[textnum]+' " )',player.x-180,(player.y-player.height)*TILE-110);
		cv.fillText("\\",player.x-100,(player.y-player.height)*TILE-90);
	}

    var socket = io();
      $('form').submit(function(){
      	if($('#m').val()!=''){
      		socket.emit('chat message', $('#m').val() );
	        $('#m').val('');
	    }
        return false;
      });
      socket.on('chat message', function(msg){
      	textnum++;
      	if(textnum>5){
      		textnum = 0;
      	}
      	var currentdate = new Date(); 
		var datetime = "from guest - time : " + currentdate.getDate() + "/"
                + (currentdate.getMonth()+1)  + "/" 
                + currentdate.getFullYear() + " @ "  
                + currentdate.getHours() + ":"  
                + currentdate.getMinutes() + ":" 
                + currentdate.getSeconds();
        $('#messages').append($('<p>').addClass('odd').text(msg));
        $('#messages').append($('<span>').text(datetime));
        $('#messages').append($('<p>').addClass('even').text(chat[textnum]));
      });
      socket.emit('count total', total);
      socket.on('count total', function(counter){
      	total = counter;
      });